DROP DATABASE product_wrapper;
CREATE DATABASE product_wrapper; 
\c product_wrapper
-- DROP SCHEMA product_schema;
-- CREATE SCHEMA product_schema;
DROP IF EXISTS TABLE images;
CREATE TABLE images (
    id SERIAL NOT NULL,
    size VARCHAR NOT NULL,
    color VARCHAR NOT NULL,
    orientation VARCHAR NOT NULL,
    url TEXT NOT NULL
);

DROP IF EXISTS TABLE shoe_size;
CREATE TABLE shoe_size (
    id SERIAL NOT NULL,
    size NUMERIC NOT NULL
);

DROP IF EXISTS TABLE products;
CREATE TABLE products (
    id SERIAL NOT NULL,
    product_name VARCHAR NOT NULL,
    company_name VARCHAR NOT NULL,
    item_number INT NOT NULL,
    color VARCHAR NOT NULL,
    price NUMERIC NOT NULL,
    rating VARCHAR NOT NULL,
    no_ratings INT NOT NULL
);

